import axios from "axios";
import React, { useEffect, useState } from "react";

const Student = () => {
  const [students, setStudents] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch student data from the backend API
    axios
      .get("http://localhost:8081/")
      .then((res) => {
        setStudents(res.data); // Update state with the fetched data
        setError(null); // Clear any previous errors
      })
      .catch((err) => {
        console.log(err);
        setError("Failed to fetch student data. Please try again later."); // Set error message in state
      });
  }, []);

  return (
    <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
      <div className="w-50 bg-white rounded p-3">
        <button className="btn btn-success">Add +</button>
        {error && <p className="text-danger">{error}</p>} {/* Display error message if any */}
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {students.map((data, i) => (
              <tr key={i}>
                <td>{data.name}</td>
                <td>{data.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Student;
