import axios from "axios";
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const Student = () => {
  const [students, setStudents] = useState([]);

  useEffect(() => {
    axios
      .get("http://localhost:8081/")
      .then((res) => setStudents(res.data)) // Update state with the fetched data
      .catch((err) => console.log(err));
  }, []);

  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this student?");
    if (confirmDelete) return;

    try {
      await axios.delete(`http://localhost:8081/delete/${id}`);
      // Update UI instead of reloading the page
      // For example, remove the student from the list in the state
    } catch (err) {
      console.error("Failed to delete the student:", err);
      // Optionally, show an error message to the user
    }
  };


  return (
    <div className="d-flex vh-100 bg-primary justify-content-center align-items-center">
      <div className="w-50 bg-white rounded p-3">
        <Link to="/create" className="btn btn-success">
          Add +
        </Link>
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Position</th>
              <th>Dept</th>
              <th>Salary</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students.map((data, i) => (
              <tr key={i}>
                <td>{data.name}</td>
                <td>{data.position}</td>
                <td>{data.dept}</td>
                <td>{data.salary}</td>
                <td>
                  <Link to={`update/${data.id}`} className="btn btn-primary">
                    Update
                  </Link>
                  <button className="btn btn-danger ms-2" onClick={e=>handleDelete(data.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Student;
